import { Outlet, Link, useLocation } from "react-router-dom";
import { useState, useEffect } from 'react';
import axios from "axios";
import { LocationState } from 'history';
import LogoutButton from "./LogoutButton";
import { useCookies } from 'react-cookie';
import Cookies from 'js-cookie';
const Layout = () => {
  const location = useLocation();
  var isLoggedIn: string | undefined = location.state?.isLoggedIn;
  useEffect(() => {
    isLoggedIn = Cookies.get('isLoggedIn');
  }, []);
  const [isLoading, setLoading] = useState(false);
  const [loggedIn, setLoggedIn] = useState(isLoggedIn);
  const [endRefresh, setEndRefresh] = useState(false);
  const handleLogout = async () => {
    setLoggedIn('false');
  }


  console.log("is loading: " + isLoading);
  console.log("is logged in: " + loggedIn);
  console.log("end refresh: " + endRefresh);

  if(isLoading){
    return <div>Loading...</div>
  }
  return (
    <>
      {isLoggedIn=='True' &&<Link to="/">Home</Link>}
      <nav>
        <ul>
          {isLoggedIn=='True' && <li>
            <Link to="/">Home</Link>
          </li>}
          {isLoggedIn!='True' &&<li>
            <Link to="/login">Login</Link>
          </li>
          }
          {isLoggedIn=='True' && <li>
            <Link to="/api/data">Data</Link>
          </li>}
          {isLoggedIn=='True' &&<li>
            <Link to="/contact">Contact</Link>
          </li>}
          {isLoggedIn=='True' &&<li>
            <Link to="/api/register">Register</Link>
          </li>}
          {isLoggedIn=='True' &&<LogoutButton onLogout={handleLogout}/>}
        </ul>
      </nav>

      <Outlet />
    </>
  )
};

export default Layout;
