import { Outlet, Link, useLocation } from "react-router-dom";
import { useState, useEffect } from 'react';
import axios from "axios";
import { LocationState } from 'history';
import LogoutButton from "./LogoutButton";
import { useCookies } from 'react-cookie';
import Cookies from 'js-cookie';
const Layout = () => {
  const location = useLocation();
  var isLoggedIn: string | undefined = Cookies.get('isLoggedIn');
  useEffect(() => {
    isLoggedIn = Cookies.get('isLoggedIn');
  }, []);
  const [isLoading, setLoading] = useState(false);
  const [loggedIn, setLoggedIn] = useState(isLoggedIn);
  const [endRefresh, setEndRefresh] = useState(false);
  const handleLogout = async () => {
    setLoggedIn('false');
  }

  console.log('----------------------------------');
  console.log("is loading: " + isLoading);
  console.log("is logged in: " + loggedIn);
  console.log("end refresh: " + endRefresh);
  console.log('----------------------------------');
  if(isLoading){
    return <div>Loading...</div>
  }
  return (
    <>
      <div className="flex">
        <div className={'w-72 h-screen bg-dark-green'}>sidebar</div>
        <div className="p-7 text-2x1 font-semibold">
          <h1>Home Page</h1>
        </div>
      </div>
        <div className="w-full md:block md:w-auto" id="navbar-default">
          <ul>
            {isLoggedIn=='True' && <li>
              <Link to="/" >Home</Link>
            </li>}
            {isLoggedIn!='True' &&<li>
              <Link to="/login">Login</Link>
            </li>
            }
            {isLoggedIn=='True' && <li>
              <Link to="/api/data">Data</Link>
            </li>}
            {isLoggedIn=='True' &&<li>
              <Link to="/contact">Contact</Link>
            </li>}
            {isLoggedIn=='True' &&<li>
              <Link to="/api/register">Register</Link>
            </li>}
            {isLoggedIn=='True' &&<LogoutButton onLogout={handleLogout}/>}
          </ul>
        </div>
      <Outlet />
    </>
  )
};

export default Layout;
