import { Outlet, Link, useLocation } from "react-router-dom";
import { useState, useEffect } from 'react';
import axios from "axios";
import { LocationState } from 'history';
import LogoutButton from "./LogoutButton";

const Layout = () => {
  const location = useLocation();
  console.log(location.state);
  const isLoggedIn: boolean = location.state?.isLoggedIn ?? false;
  const [isLoading, setLoading] = useState(false);
  const [loggedIn, setLoggedIn] = useState(isLoggedIn);
  const [endRefresh, setEndRefresh] = useState(false);
  console.log(isLoggedIn);
  const handleLogout = async () => {
    setLoggedIn(false);
  }



  console.log("is loading: " + isLoading);
  console.log("is logged in: " + loggedIn);
  console.log("end refresh: " + endRefresh);

  if(isLoading){
    return <div>Loading...</div>
  }
  return (
    <>
      {loggedIn &&<Link to="/">Home</Link>}
      <nav>
        <ul>
          {loggedIn && <li>
            <Link to="/">Home</Link>
          </li>}
          {!loggedIn &&<li>
            <Link to="/login">Login</Link>
          </li>
          }
          {loggedIn && <li>
            <Link to="/api/data">Data</Link>
          </li>}
          {loggedIn &&<li>
            <Link to="/contact">Contact</Link>
          </li>}
          {loggedIn &&<li>
            <Link to="/api/register">Register</Link>
          </li>}
          {loggedIn &&<LogoutButton onLogout={handleLogout}/>}
        </ul>
      </nav>

      <Outlet />
    </>
  )
};

export default Layout;
