import { Outlet, Link, useLocation } from "react-router-dom";
import { useState, useEffect } from 'react';
import axios from "axios";
import { LocationState } from 'history';
import LogoutButton from "./LogoutButton";

const Layout = () => {
  const location = useLocation<LocationState>();
  const [isLoading, setLoading] = useState(false);
  const [loggedIn, setLoggedIn] = useState(false);
  const [first, setFirst] = useState(true);
  const [endRefresh, setEndRefresh] = useState(false);
  var loginStatus: boolean = false; 
  async function fetchSession(){
    setLoading(true);
    const response = await fetch('http://localhost:5000/api/session',
    {
      credentials: 'include'
    });
    const data = await response.json();
    console.log("username" + data.username);
    setLoading(false);
    if(data.username){
      console.log('is logged in')
      setLoggedIn(true);
      setEndRefresh(true);
      return true;
    }
    else{
      console.log('is not logged in')
      return false;
    }
  }
  const handleLogout = async () => {
    setLoggedIn(false);
  }

  if(!loggedIn && first && !endRefresh){
    fetchSession();
    setFirst(false);
  }

  console.log("is loading: " + isLoading);
  console.log("is logged in: " + loggedIn);
  console.log("first: " + first);
  console.log("end refresh: " + endRefresh);

  if(isLoading){
    return <div>Loading...</div>
  }
  return (
    <>
      {loggedIn &&<Link to="/">Home</Link>}
      <nav>
        <ul>
          {loggedIn && <li>
            <Link to="/">Home</Link>
          </li>}
          {!loggedIn &&<li>
            <Link to="/login">Login</Link>
          </li>
          }
          {loggedIn && <li>
            <Link to="/api/data">Data</Link>
          </li>}
          {loggedIn &&<li>
            <Link to="/contact">Contact</Link>
          </li>}
          {loggedIn &&<li>
            <Link to="/api/register">Register</Link>
          </li>}
          {loggedIn &&<LogoutButton onLogout={handleLogout}/>}
        </ul>
      </nav>

      <Outlet />
    </>
  )
};

export default Layout;
